import { useEffect } from "react";
import { useState } from "react";
import Card from "../components/Card";

const Photos = () => {
  const [photos, setPhotos] = useState([]);
  const [sort, setSort] = useState("asc");
  const [submited, setSubmited] = useState("");
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const deletePhoto = (id) => {
    // TODO: answer here
  };

  useEffect(() => {
    setLoading(true);
    // TODO: answer here
    switch (sort) {
      case "asc":
        async function searchData (){
          try{
            const searchItem = await window.fetch('https://gallery-app-server.vercel.app/photos');
            const result = await searchItem.json();
            setPhotos(result);
            setLoading(false);
          } catch (error) { 
            setError(error) 
          }

          if(search){
            async function dataOne (){
              try{
                const searchItem = await window.fetch('https://gallery-app-server.vercel.app/photos?q=' + search);
                const result = await searchItem.json();
                setPhotos(result);
                setLoading(false);
              } catch (error) { 
                setError(error) 
              }
            }
            dataOne();
          }
        }
        searchData();
      case "desc":
        async function searchDataOne (){
          try{
            const searchItem = await window.fetch('https://gallery-app-server.vercel.app/photos/?_sort=id&_order=desc');
            const result = await searchItem.json();
            setPhotos(result)
            setLoading(false);
            console.log(result, "descending results");
          } catch (error) { 
            setError(error) 
          }

          if(search){
            async function dataOne (){
              try{
                const searchItem = await window.fetch("https://gallery-app-server.vercel.app/photos?_sort=id&_order=desc&q=" + search);
                const result = await searchItem.json();
                setPhotos(result);
                setLoading(false);
              } catch(error) { 
                setError(error) 
              }
            }
            dataOne();
          }
        }
        searchDataOne()
    }
  }, [sort, search]);


  useEffect(() => {
    setLoading(true);
    // TODO: answer here
  }, []);

  if (error) return <h1 style={{ width: "100%", textAlign: "center", marginTop: "20px" }} >Error!</h1>;

  return (
    <>
    <div className="container">
      <div className="options">
        <select
          onChange={(e) => setSort(e.target.value)}
          data-testid="sort"
          className="form-select"
          style={{}}
        >
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            setSubmited(search);
          }}
        >
          <input
            type="text"
            data-testid="search"
            onChange={(e) => setSearch(e.target.value)}
            className="form-input"
          />
          <input
            type="submit"
            value="Search"
            data-testid="submit"
            className="form-btn"
          />
        </form>
      </div>
      <div className="content">
        {loading ? (
          <h1
            style={{ width: "100%", textAlign: "center", marginTop: "20px" }}
          >
            Loading...
          </h1>
        ) : (
          photos.map((photo) => {
            return (
              <Card key={photo.id} photo={photo} deletePhoto={deletePhoto} />
            );
          })
        )}
      </div>
    </div>
  </>
  );
};

export default Photos;
